main()
{
        execute_dir=$1
        repo=$2
        var_name=$3
        mod_name=`basename $execute_dir`
	branch_name=$4
        cd $execute_dir
        if [ $repo = "GIT" ]
        then
                changeset=`git show | grep "^commit" | cut -d " " -f2`
        elif [ $repo = "HG" ]
        then
                changeset=`hg parent | grep changeset: | cut -d " " -f4 | cut -d ":" -f2`
        fi
	echo CLONE_CHANGESET="$mod_name:$changeset::${branch_name}"

}

main $*
