echo "<html><head></head><body>"
echo "<table border=1>"
echo "<tr><td>BinaryName</td><td>Md5sum</td></tr>"
data=`cat $1 | grep "BINARY_DETAILS"`
exact_data=`echo $data | cut -d "=" -f2`

for i in `echo ${exact_data} | tr "," " "`
do
        bname=`echo $i | cut -d ":" -f1`
        md5=`echo $i | cut -d ":" -f3`
        echo "<tr>"
        echo "  <td>$bname</td>"
        echo "  <td>$md5</td>"
        echo "</tr>"

done
echo "</table>"
echo "</body></html>"
