is_project_folder=$1
rce_is_matched_list=$(grep -irEl "\= \"cmd.exe\""\|"\= \"command.com\"" ${is_project_folder})
if [ $? -eq 0 ]
then
	for flist in $(echo $rce_is_matched_list)
	do
		basename ${flist} | grep -i ".rul"
		if [ $? -eq 0 ]
		then
		sed -i "s@\= \"cmd.exe\"@\= \WINSYSDIR \^ \"cmd.exe\"@g" $flist
		sed -i "s@\=\"cmd.exe\"@\=\WINSYSDIR \^ \"cmd.exe\"@g" $flist
		sed -i "s@\= \"command.com\"@\= \WINSYSDIR \^ \"command.com\"@g" $flist
		sed -i "s@\=\"command.com\"@\=\WINSYSDIR \^ \"command.com\"@g" $flist
		fi
	done
fi
