const lyteParser = require('@zoho/slyte-doc');
const fs = require('fs');
(async function () {
  try {
    var data = await lyteParser({
      src: "./components/javascript"
    });
    if(data.error) {
      console.error("Error occurred while compiling comments", data.error);
    }
    else {
      fs.writeFileSync('./ui-comp-property.json', JSON.stringify(data, null, 4));
    }
  }
  catch(error) {
    console.error("Error occurred while compiling comments", error);
  }
})();